{{-- @block id="card" label="Card" description="A card component with image, title, and description" --}}
<div class="max-w-sm mx-auto bg-white rounded-xl shadow-lg overflow-hidden border border-purple-100" data-gjs-type="default" data-gjs-draggable="true" data-gjs-droppable="false">
    <img src="https://via.placeholder.com/400x200/8b5cf6/ffffff?text=Card+Image" alt="Card image" class="w-full h-48 object-cover" data-gjs-type="image" data-gjs-name="card-image"/>
    <div class="p-6">
        <h3 class="text-xl font-bold mb-2 text-purple-900" data-gjs-type="text" data-gjs-name="card-title">Card Title</h3>
        <p class="text-gray-600 mb-4" data-gjs-type="text" data-gjs-name="card-description">Card description goes here. You can edit this text to add your content.</p>
        <button class="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors font-semibold" data-gjs-type="text" data-gjs-name="card-button">Learn More</button>
    </div>
</div> 